"pct exec 115 -- bash -c '
useradd -m -s /bin/bash claude 2>/dev/null || echo \"User claude already exists\";
echo \"claude:claude\" | chpasswd;
usermod -aG sudo claude 2>/dev/null || true;
echo \"claude ALL=(ALL) NOPASSWD:ALL\" > /etc/sudoers.d/claude;
chmod 440 /etc/sudoers.d/claude;
echo \"Claude user configured successfully in lxc-jellyfin-1\";
id claude
'" && echo "✅ lxc-jellyfin-1 configured successfully via PCT"

cat > /tmp/ssh_proxmox.exp << 'EOF'
#!/usr/bin/expect -f
set timeout 30

spawn ssh -o StrictHostKeyChecking=no root@192.168.0.199
expect {
    "password:" {
        send "pedro\r"
        expect "# " {
            send "echo 'Successfully connected to Proxmox host'\r"
            expect "# "
            send "hostname\r" 
            expect "# "
            send "pct list\r"
            expect "# "
            send "exit\r"
        }
    }
    timeout {
        puts "Connection timeout"
    }
}
EOF
chmod +x /tmp/ssh_proxmox.exp && \
echo "Expect script created, testing connection..." && \
/tmp/ssh_proxmox.exp

echo "=== Verifying claude user configuration on lxc-jellyfin-1 ===" && \
